import React, { useState, useEffect, useContext } from "react";
import UserContext from "./UserContext";
import ScareBnBApi from "./api";
import ListingCard from "./ListingCard";

/**Shows homepage
 *
 * useContext= user
 * props=none
 * state=none
 *
 * Routes --> Homepage
 */

function Homepage() {
  console.log("homepage");
  const {currUser, token} = useContext(UserContext)
  const [guestToken, setGuestToken] = useState(ScareBnBApi.token)
  const [listings, setListings] = useState({
    data: null,
    isLoading: true,
  });

  useEffect(function getTokenAndListingsOnMount() {
    console.log('getGuestTokenOnMount + getListingsOnMount');

    async function fetchGuestTokenAndListings() {
      try {
        const response = await ScareBnBApi.is_guest();
        setGuestToken(response.token);
        ScareBnBApi.token = response.token;
        console.log('token', ScareBnBApi.token);

        const listingsRes = await ScareBnBApi.getListingsForGuest();
        setListings({ data: listingsRes, isLoading: false });

      } catch (err) {
        console.error("Error fetching token or listings", err);
      }
    }

    fetchGuestTokenAndListings();
  }, []);

  if(listings.isLoading === true) {
    return <p>Loading...</p>
  }


  return (
    <div className="Homepage">
      {listings.data.map((listing) => (
        <ListingCard key={listing.id} listing={listing} />
      ))}
    </div>
  );
}

export default Homepage;
